    AskModalShow() {
        this.setState({
            ask_show: true,
        });
    }

    AskModalClose() {
        this.setState({
            ask_show: false,
        });
    }

    //-----------------------------------
    //Four functions to continues create new pomodoros when one is completed
    ContinueModal() {
