import * as React from 'react';
import { Container, Row, Col, Button, Modal } from 'react-bootstrap';
import Card from '../components/Card/Card';

import { connect } from 'react-redux';
import moment from 'moment';
import { fetchAllProjects, clearErrorMessage } from '../redux/actionCreators/project.action';

import { sessionService } from '../services/session.service';

import OptionList from '../components/List/OptionList';

import working1 from './../assets/img/working1.svg';
import working2 from './../assets/img/working2.svg';
import chill1 from './../assets/img/chill1.svg';
import chill2 from './../assets/img/chill2.svg';

const imgs = [working1, working2, chill1, chill2];

const mapStateToProps = (state: any) => {
    return {
        projectlist: state.project.list,
        projectErrMess: state.project.errMess
    };
};

const mapDispatchToProps = dispatch => ({
    fetchAllProjects: userId => {
        dispatch(fetchAllProjects(userId));
    },
    clearErrorMessage: () => {
        dispatch(clearErrorMessage());
    }
});

interface Props {
    projectlist: any,
    projectErrMess: any,
    fetchAllProjects: any,
    clearErrorMessage: any
};

interface State {
    minutes: number,
    seconds: number,
    hours: number,
    total_seconds: number,
    associated_id: number,
    active_session_id: number,
    /*
    set_minutes: number,
    set_seconds: number,
    set_hours: number,

    break_minutes: number,
    break_seconds: number,
    break_hours: number,
    */

    start_time: String,
    end_time: String,
    //total_hours: number,

    create_show: boolean,
    continue_show: boolean,
    stop_show: boolean,
    ask_show: boolean,
    active: boolean,
    counter: number,
    is_break: boolean,

    error_message: any
};

class Pomodoro extends React.Component<Props, State>{
    private curImg: React.RefObject<HTMLImageElement>;
    //-----------------------------------
    //Three functions for modals to create new pomodoro
    CreateModal() {
        return <Modal id="create_modal" show={this.state.create_show} onHide={this.CreateModalClose}>
            <Modal.Header>
                <Modal.Title>Set duration for your pomodoro and break</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Row>Set duration for your pomodoro</Row>
                <Row>
                    <Button id="create_hour_minus_one" variant="outline-primary" onClick={()=>{this.MinusButton(2);}}></Button>
                    <input
                        id="create_hours"
                        value={this.state.hours}
                        onChange={(e: any) => {
                            this.setState({hours: e.target.value % 100 });
                        }} />
                    <Button id="create_hour_plus_one" variant="outline-primary" onClick={()=>{this.MinusButton(2);}}></Button>
                    hour(s)
                </Row>
                <Row>
                    <Button id="create_minute_minus_ten" variant="outline-primary" onClick={()=>{this.MinusButton(1);}}></Button>
                    <input
                        id="create_minutes"
                        value={this.state.minutes}
                        onChange={(e: any) => {
                            this.setState({minutes: e.target.value % 100 });
                        }} />
                    <Button id="create_minute_plus_ten" variant="outline-primary" onClick={()=>{this.PlusButton(1);}}></Button>
                    minute(s)
                </Row>
                <Row>
                    <Button id="create_second_minus_ten" variant="outline-primary" onClick={()=>{this.MinusButton(0);}}></Button>
                    <input
                        id="create_second"
                        value={this.state.seconds}
                        onChange={(e: any) => {
                            this.setState({seconds: e.target.value % 100 });
                        }} />
                    <Button id="create_second_plus_ten" variant="outline-primary" onClick={()=>{this.PlusButton(0);}}></Button>
                    second(s)
                </Row>
            </Modal.Body>
            <Modal.Footer>
                <Button id="create_cancel_button" variant="secondary" onClick={this.CreateCancel}>
                    Cancel
                </Button>
                <Button id="create_confirm_button" variant="primary" onClick={this.CreateSubmit}
                    disabled={this.state.hours == 0 && this.state.minutes == 0 && this.state.seconds == 0}> Submit </Button>
            </Modal.Footer>
        </Modal>
    }

    CreateModalShow() {
        this.setState({
            create_show: true
        });
    }

    CreateModalClose() {
        this.setState({
            create_show: false,
        });
    }

    //-----------------------------------
    //Three functions representing the action of user on create modal
    CreateSubmit() {
        var time = moment().format('YYYY-MM-DDTHH:mmZ');
        console.log(time);
        this.setState({
            start_time: time
        })
        if (this.state.associated_id != -1) {
            this.CreateSession();
        }
        this.CreatePomodoro();
        this.CreateModalClose();
    }
    CreateCancel() {
        this.TimeClear();
        this.CreateModalClose();
    }
    ChangeSelect(e: any) {
        this.setState({
            associated_id: e.target.value
        })
    }
    //-----------------------------------
    //Confirm the mode for next step, keep working or working or taking a break
    AskModal(){
        return (
            <Modal id="ask_modal" show={this.state.ask_show}>
            <Modal.Header>
                Please select whether you want to take a break or keep working
            </Modal.Header>
            <Modal.Body>
                <Row>
                    <Col># of pomodoro completed</Col>
                    <Col>{this.state.counter}</Col>
                </Row>
                <Row>
                    <Col>Session Start time</Col>
                    <Col>{this.state.start_time}</Col>
                </Row>
            </Modal.Body>
            <Modal.Footer>
                <Button id="decide_stop" variant="secondary" onClick={()=>{
                    this.ContinueOver();
                    this.AskModalClose();
                }}>
                    Stop this session
                </Button>
                <Button id="decide_break" variant="outline-primary" onClick={()=>{
                    this.setState({
                        is_break: true,
                    });
                    this.ContinueModalShow();
                    this.AskModalClose();
                }}>
                    Take a break
                </Button>

                <Button id="decide_work" variant="primary" onClick={()=>{
                    this.ContinueModalShow();
                    this.AskModalClose();
                }}>
                    Continue to work
                </Button>
            </Modal.Footer>
            </Modal>
        )
    }

    AskModalShow(){
        this.setState({
            ask_show: true,
        });
    }

    AskModalClose(){
        this.setState({
            ask_show: false,
        });
    }

    //-----------------------------------
    //Four functions to continues create new pomodoros when one is completed
    ContinueModal() {
        return (
            <Modal id="continue_modal" show={this.state.continue_show} onHide={this.ContinueOver}>
                <Modal.Header>
                    Please select your time for your {this.state.is_break ? "break" : "work"} in this session
                </Modal.Header>
                <Modal.Body>
                    <Row>
                        <Button id="continue_hour_minus_one" variant="outline-primary" onClick={()=>{this.setState({hours: this.state.hours - 10});}}></Button>
                        <input
                            id="continue_hours"
                            value={this.state.hours}
                            onChange={(e: any) => {
                                this.setState({ hours: e.target.value % 100 });
                            }}
                            />
                        <Button id="continue_hour_plus_one" variant="outline-primary" onClick={()=>{this.MinusButton(2);}}></Button>
                        hour(s)
                    </Row>
                    <Row>
                        <Button id="continue_minute_minus_ten" variant="outline-primary" onClick={()=>{this.PlusButton(2);}}></Button>
                        <input
                            id="continue_minutes"
                            value={this.state.minutes}
                            onChange={(e: any) => {
                                this.setState({ minutes: e.target.value % 100 });
                            }} 
                            />
                        <Button id="continue_minute_plus_ten" variant="outline-primary" onClick={()=>{this.MinusButton(1);}}></Button>
                        minute(s)
                    </Row>
                    <Row>
                        <Button id="continue_second_minus_ten" variant="outline-primary" onClick={()=>{this.PlusButton(1);}}></Button>
                        <input
                            id="continue_second"
                            value={this.state.seconds}
                            onChange={(e: any) => {
                                this.setState({ seconds: e.target.value % 100 });
                            }} 
                            />
                        <Button id="continue_second_plus_ten" variant="outline-primary" onClick={()=>{this.PlusButton(0);}}></Button>

                        second(s)
                    </Row>

                </Modal.Body>
                <Modal.Footer>
                    <Button id="continue_cancel_button" variant="secondary" onClick={(this.ContinueOver)}>
                        No
                    </Button>
                    <Button id="continue_confirm_button" variant="primary" onClick={this.Continue} disabled={this.state.hours == 0 && this.state.minutes == 0 && this.state.seconds == 0}>
                        Yes
                    </Button>
                </Modal.Footer>
            </Modal>
        )
    }
    ContinueModalShow() {
        this.setState({
            continue_show: true
        });
    }

    ContinueModalClose() {
        this.setState({
            continue_show: false
        })
    }

    //-----------------------------------
    //two functions representing two actions user chooses
    Continue() {
        this.ContinueModalClose();
        this.CreatePomodoro();
    }

    ContinueOver() {
        this.ContinueModalClose();
        if (this.state.associated_id != -1) {
            this.UpdateSession();
        }
        this.TimeClear();
        this.StopSession();
        //console.log(this.state.associated_id);
    }

    //--------------------------------
    //Three functions to control the stop modalx
    StopModal() {
        return (
            <Modal id="stop_modal" show={this.state.stop_show} onHide={this.StopModalClose}>
                <Modal.Header>
                    You are going to stop current pomodoro.
                </Modal.Header>
                <Modal.Body>
                    Are you going to include the runtime of current incomplete pomodoro?
                    <Row>
                        <Col># of pomodoro completed</Col>
                        <Col>{this.state.counter}</Col>
                    </Row>
                    <Row>
                        <Col>Session Start time</Col>
                        <Col>{this.state.start_time}</Col>
                    </Row>
                </Modal.Body>
                <Modal.Footer>
                    <Button
                        id="stop_cancel_button"
                        onClick={this.StopCancel}
                        variant="secondary">
                        No
                    </Button>
                    <Button
                        id="stop_confirm_button"
                        onClick={this.StopConfirm}
                        variant="primary">
                        Yes
                    </Button>
                </Modal.Footer>
            </Modal>
        )
    }
    StopModalShow() {
        this.setState({
            stop_show: true
        })
    }

    StopModalClose() {
        this.setState({
            stop_show: false
        })
    }


    //-----------------------------------
    //Two functions representing different action of user in StopModal
    StopCancel() {
        this.StopModalClose();
        if (this.state.associated_id != -1) {
            this.UpdateSession();
        }
        this.TimeClear();
        //@ts-ignore
        clearInterval(this.interval);
        this.StopSession();
    }

    StopConfirm() {
        this.StopModalClose();
        var time = moment().format('YYYY-MM-DDTHH:mmZ');
        this.setState({
            end_time: time,
            counter: this.state.counter + 1
        }, ()=>{
            if (this.state.associated_id != -1) {
                this.UpdateSession();
            }
            this.TimeClear();
            //@ts-ignore
            clearInterval(this.interval);
            this.StopSession();
        });
    }
    //-----------------------------------
    //Functions for the action of buttons beside each text field
    MinusButton(mode: number){
        var temp_seconds = this.state.seconds;
        var temp_minutes = this.state.minutes;
        var temp_hours = this.state.hours;
        switch(mode){
            case 0:
                if(temp_seconds <= 10){
                    temp_seconds = 0;
                }
                else{
                    temp_seconds -= 10;
                }
                break;
            case 1:
                if(temp_minutes <= 10){
                    temp_minutes = 0;
                }
                else{
                    temp_minutes -= 10;
                }
                break;
            case 2:
                if(temp_hours != 0){
                    temp_hours -= 1;
                }
                break;
            default:
                break;
        }
        this.setState({
            seconds: temp_seconds,
            minutes: temp_minutes,
            hours: temp_hours,
        });
    }

    PlusButton(mode: number){
        var temp_seconds = this.state.seconds;
        var temp_minutes = this.state.minutes;
        var temp_hours = this.state.hours;
        switch(mode){
            case 0:
                if(temp_seconds >= 50){
                    temp_seconds -= 50;
                    temp_minutes += 1;
                    if(temp_minutes >= 60){
                        temp_minutes -= 60;
                        temp_hours = (temp_hours == 99) ? 99 : temp_hours + 1;
                    }
                }
                else{
                    temp_seconds += 10;
                }
                break;
            case 1:
                if(temp_minutes >= 50){
                    temp_minutes -= 50;
                    temp_hours = (temp_hours == 99) ? 99 : temp_hours + 1;
                }
                else{
                    temp_minutes += 10;
                }
                break;
            case 2:
                if(temp_hours != 99){
                    temp_hours += 1;
                }
                break;
            default:
                break;
        }
        this.setState({
            seconds: temp_seconds,
            minutes: temp_minutes,
            hours: temp_hours,
        });
    }

