    //-----------------------------------
    //Three functions for tracking time
    TimeClear() {
        this.setState({
            hours: 0,
            minutes: 0,
            seconds: 0,

            total_seconds: 0,
        });
    }

    StopSession(){
        this.setState({
            active: false,
            is_break: false,
            counter: 0
        })
    }

    CreatePomodoro(){
        var temp_seconds = this.state.seconds;
        var temp_minutes = this.state.minutes;
        var temp_hours = this.state.hours;
        if(temp_seconds >= 60){
            temp_seconds -= 60;
            temp_minutes += 1;
        }
        if(temp_minutes >= 60){
            temp_minutes -= 60;
            temp_hours += 1;
        }
        this.setState({
            active: true,
            total_seconds: temp_hours * 3600 + temp_minutes * 60 + temp_seconds,
            hours: temp_hours,
            minutes: temp_minutes,
            seconds: temp_seconds,
        });

        //@ts-ignore
        this.interval = setInterval(this.Countdown, 1000);
    }

    Countdown() {
        if (this.state.active) {
            var seconds = this.state.seconds;
            var minutes = this.state.minutes;
            var hours = this.state.hours;
            var total_seconds = this.state.total_seconds;
            seconds--;
            total_seconds--;
            //console.log("here");
            if (seconds < 0) {
                seconds += 60;
                minutes -= 1;
            }
            if (minutes < 0) {
                minutes += 60;
                hours -= 1;
            }
            this.setState({
                seconds: seconds,
                hours: hours,
                minutes: minutes,
                total_seconds: total_seconds
            });
            if (total_seconds <= 0) {
                //@ts-ignore
                clearInterval(this.interval);
                var time = moment().format('YYYY-MM-DDTHH:mmZ');
                if(this.state.is_break){
                    this.setState({
                        end_time: time,
                        active: false,
                        is_break: false,
                    })
                }
                else{
                    this.setState({
                        end_time: time,
                        counter: this.state.counter + 1,
                        active: false,
                        is_break: false
                    });
                }
                this.AskModalShow();
            }
        }
    }

